import React, { useState } from "react";
import { predictLoan } from "../api";

function LoanForm({ setResult }) {
  const [form, setForm] = useState({
    Gender: "Male",
    Married: "No",
    Dependents: "0",
    Education: "Graduate",
    Self_Employed: "No",
    Property_Area: "Urban",
    Credit_History: "1",
    ApplicantIncome: "",
    CoapplicantIncome: "",
    Loan_Amount_Term: "360",
    DesiredLoanAmount: "",
  });

  const [showLoanType, setShowLoanType] = useState(false);
  const [loanType, setLoanType] = useState("Home Loan");

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        ...form,
        ApplicantIncome: parseInt(form.ApplicantIncome) || 0,
        CoapplicantIncome: parseInt(form.CoapplicantIncome) || 0,
        Loan_Amount_Term: parseInt(form.Loan_Amount_Term) || 360,
        Credit_History: parseInt(form.Credit_History) || 1,
        DesiredLoanAmount: parseInt(form.DesiredLoanAmount) || 0,
      };
      const res = await predictLoan(payload);
      setResult(res.data);
      if (res.data.eligible) setShowLoanType(true);
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  const handleLoanTypeSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        ...form,
        ApplicantIncome: parseInt(form.ApplicantIncome) || 0,
        CoapplicantIncome: parseInt(form.CoapplicantIncome) || 0,
        Loan_Amount_Term: parseInt(form.Loan_Amount_Term) || 360,
        Credit_History: parseInt(form.Credit_History) || 1,
        DesiredLoanAmount: parseInt(form.DesiredLoanAmount) || 0,
        LoanType: loanType,
      };
      const res = await predictLoan(payload);
      setResult(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  return (
    <div className="loan-form-container">
      <form onSubmit={handleSubmit} className="loan-form">
        <h2>Check Your Loan Eligibility</h2>

        {/* Applicant Details */}
        <div className="input-group">
          <label>Gender</label>
          <select name="Gender" value={form.Gender} onChange={handleChange}>
            <option>Male</option>
            <option>Female</option>
          </select>
        </div>

        <div className="input-group">
          <label>Marital Status</label>
          <select name="Married" value={form.Married} onChange={handleChange}>
            <option>No</option>
            <option>Yes</option>
          </select>
        </div>

        <div className="input-group">
          <label>Dependents</label>
          <select name="Dependents" value={form.Dependents} onChange={handleChange}>
            <option>0</option>
            <option>1</option>
            <option>2</option>
            <option>3+</option>
          </select>
        </div>

        <div className="input-group">
          <label>Education</label>
          <select name="Education" value={form.Education} onChange={handleChange}>
            <option>Graduate</option>
            <option>Not Graduate</option>
          </select>
        </div>

        <div className="input-group">
          <label>Self Employed</label>
          <select name="Self_Employed" value={form.Self_Employed} onChange={handleChange}>
            <option>No</option>
            <option>Yes</option>
          </select>
        </div>

        <div className="input-group">
          <label>Property Area</label>
          <select name="Property_Area" value={form.Property_Area} onChange={handleChange}>
            <option>Urban</option>
            <option>Semiurban</option>
            <option>Rural</option>
          </select>
        </div>

        <div className="input-group">
          <label>Credit History (1=Good, 0=Bad)</label>
          <select name="Credit_History" value={form.Credit_History} onChange={handleChange}>
            <option>1</option>
            <option>0</option>
          </select>
        </div>

        {/* Income & Loan */}
        <div className="input-group">
          <label>Applicant Income (₹)</label>
          <input type="number" name="ApplicantIncome" value={form.ApplicantIncome} onChange={handleChange} placeholder="0" />
        </div>

        <div className="input-group">
          <label>Coapplicant Income (₹)</label>
          <input type="number" name="CoapplicantIncome" value={form.CoapplicantIncome} onChange={handleChange} placeholder="0" />
        </div>

        <div className="input-group">
          <label>Loan Amount Term (months)</label>
          <input type="number" name="Loan_Amount_Term" value={form.Loan_Amount_Term} onChange={handleChange} placeholder="360" />
        </div>

        <div className="input-group">
          <label>Desired Loan Amount (₹)</label>
          <input type="number" name="DesiredLoanAmount" value={form.DesiredLoanAmount} onChange={handleChange} placeholder="0" />
        </div>

        <button type="submit" className="primary-btn">Check Eligibility</button>
      </form>

      {/* Loan Type Selection Only If Eligible */}
      {showLoanType && (
        <form onSubmit={handleLoanTypeSubmit} className="loan-type-form">
          <label>Select Loan Type</label>
          <select value={loanType} onChange={(e) => setLoanType(e.target.value)}>
            <option>Home Loan</option>
            <option>Car Loan</option>
            <option>Personal Loan</option>
          </select>
          <button type="submit" className="secondary-btn">Get Loan Offers</button>
        </form>
      )}
    </div>
  );
}

export default LoanForm;
